from logicf.StudyBuddy import get_response
import streamlit as st


# Paths to user & chatbot images
user_image = "data:image/png;base64,"
chatbot_image = "data:image/jpeg;base64,"  

with st.sidebar:
    st.markdown("## üìö Study Buddy")
    st.markdown("---")
    
# --------
# Navigation links
# col1, col2, col3 = st.columns(3)

# with col1:
#     st.page_link("main.py", label="Home", icon="üè†")

# with col2:
#     st.page_link("Pages/2-chat.py", label="ChatBot", icon="ü§ñ")

